{
    "name": "search-flights",
    "description": "Search available flights as per simple search criteria.",
    "inputSchema": "{
  "$schema" : "https://json-schema.org/draft/2020-12/schema",
  "type" : "object",
  "properties" : {
    "airShopRequest" : {
      "type" : "object",
      "description" : "Search Flight request",
      "properties" : {
        "origin" : {
          "type" : "string",
          "description" : "Departure airport",
          "enum" : [ "JFK", "LAX", "DEL", "BOM" ],
          "example" : "DEL"
        },
        "destination" : {
          "type" : "string",
          "description" : "Arrival airport",
          "enum" : [ "JFK", "LAX", "DEL", "BOM" ],
          "example" : "JFK"
        },
        "departureDate" : {
          "type" : "string",
          "format" : "date",
          "description" : "Departure Date",
          "example" : "2025-04-21"
        }
      },
      "required" : [ "departureDate", "destination", "origin" ]
    }
  },
  "required" : [ "airShopRequest" ],
  "additionalProperties" : false
}",
    "returnDirect": false,
    "baseUrl": "http://localhost:8092",
    "httpMethod": "POST",
    "defaultHeaders": {
        "Accept": [
            "application/json"
        ],
        "Content-Type": [
            "application/json"
        ]
    },
    "path": "/v1/airline/flights",
    "bodyArg": "airShopRequest"
}

